import React, { useState, useEffect } from 'react';
import './InfiniteMenu.css';

interface InfiniteMenuItem {
  image: string;
  title: string;
  description: string;
  link: string;
}

interface InfiniteMenuProps {
  items: InfiniteMenuItem[];
}

export default function InfiniteMenu({ items = [] }: InfiniteMenuProps) {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isAutoPlaying, setIsAutoPlaying] = useState(true);

  useEffect(() => {
    if (!isAutoPlaying || items.length === 0) return;

    const interval = setInterval(() => {
      setCurrentIndex((prev) => (prev + 1) % items.length);
    }, 3000);

    return () => clearInterval(interval);
  }, [isAutoPlaying, items.length]);

  const handleItemClick = (index: number) => {
    setCurrentIndex(index);
    setIsAutoPlaying(false);
  };

  const handleButtonClick = () => {
    if (!items[currentIndex]?.link) return;
    if (items[currentIndex].link.startsWith('http')) {
      window.open(items[currentIndex].link, '_blank');
    } else {
      console.log('Internal route:', items[currentIndex].link);
    }
  };

  if (items.length === 0) {
    return (
      <div className="infinite-menu-placeholder">
        <p>No items to display</p>
      </div>
    );
  }

  return (
    <div className="infinite-menu-container">
      {/* 3D Grid Background */}
      <div className="grid-background">
        {Array.from({ length: 20 }).map((_, i) => (
          <div key={i} className="grid-item" />
        ))}
      </div>

      {/* Project Cards */}
      <div className="project-cards">
        {items.map((item, index) => (
          <div
            key={index}
            className={`project-card ${index === currentIndex ? 'active' : ''}`}
            onClick={() => handleItemClick(index)}
            style={{
              transform: `rotateY(${(index - currentIndex) * 45}deg) translateZ(${Math.abs(index - currentIndex) * 100}px)`,
              opacity: Math.abs(index - currentIndex) <= 2 ? 1 : 0.3,
            }}
          >
            <div className="card-image">
              <img src={item.image} alt={item.title} />
            </div>
            <div className="card-content">
              <h3>{item.title}</h3>
              <p>{item.description}</p>
            </div>
          </div>
        ))}
      </div>

      {/* Active Item Info */}
      {items[currentIndex] && (
        <div className="active-item-info">
          <h2 className="face-title active">
            {items[currentIndex].title}
          </h2>
          <p className="face-description active">
            {items[currentIndex].description}
          </p>
          <div onClick={handleButtonClick} className="action-button active">
            <p className="action-button-icon">&#x2197;</p>
          </div>
        </div>
      )}

      {/* Navigation Dots */}
      <div className="navigation-dots">
        {items.map((_, index) => (
          <button
            key={index}
            className={`dot ${index === currentIndex ? 'active' : ''}`}
            onClick={() => handleItemClick(index)}
          />
        ))}
      </div>
    </div>
  );
}
